class Database_Model_Persona extends Database_Model_Base_Persona
{
    public static function getById($id)
    {
        return Doctrine_Core::getTable(get_class())
            ->findOneBy("id_persona", $id);
    }
    public static function getAllArrCli()
    {
        $arr = array();
        $arr["2"]="Tienda";
        $arr["10052"]="Venta de Mayoreo";
        foreach (Doctrine_Query::create()
                     ->from(get_class())
                     ->where("id_usuario_tipo='1'")
                     ->orderBy("st_idcliente asc")
                     ->execute() as $obj) {
            $arr[$obj->id_persona] = $obj->nombre." ".$obj->ap_paterno." ".$obj->ap_materno;
        }
        return $arr;
    }
}