class Database_Model_UsuarioTipo extends Database_Model_Base_UsuarioTipo
{
    public static function getAllArrusu($usu)
    {

        if($usu==4){//si es de tipo empleado
            $arr = array();
            $arr['']="Escoger";
            foreach (Doctrine_Query::create()
                         ->from(get_class())
                         ->where("usuario_tipo ='Cliente'")
                         ->orderBy("usuario_tipo Desc")
                         ->execute() as $obj) {
                $arr[$obj->id_usuario_tipo] = $obj->usuario_tipo;
            }
            return $arr;
        }else{
            $arr = array();
            $arr['']="Escoger";
            foreach (Doctrine_Query::create()
                         ->from(get_class())
                         ->where("usuario_tipo <> 'Proveedor'")
                         ->orderBy("usuario_tipo Desc")
                         ->execute() as $obj) {
                $arr[$obj->id_usuario_tipo] = $obj->usuario_tipo;
            }
            return $arr;
        }

    }
    public static function getAllArr()
    {

        $arr = array();
        $arr['']="Escoger";
        foreach (Doctrine_Query::create()
                     ->from(get_class())
                     ->where("usuario_tipo <> 'Proveedor'")
                     ->orderBy("usuario_tipo Desc")
                     ->execute() as $obj) {
            $arr[$obj->id_usuario_tipo] = $obj->usuario_tipo;
        }
        return $arr;
    }
    public static function getAllArrProv()
    {
        $arr = array();

        foreach (Doctrine_Query::create()
