class Database_Model_Usuario extends Database_Model_Base_Usuario
{
    public $super_secure = false;

    function getAll()
    {
        return Doctrine_Core::getTable("Database_Model_Usuario")->findAll();
    }
    public static function getAllarr()
    {
        $arr = array();
        $arr[""]="Selecciona";
        foreach (Doctrine_Query::create()
                     ->from(get_class())
                     ->where("status='ACTIVO'")
                     ->orderBy("id_usuario desc")
                     ->execute() as $obj) {
            $arr[$obj->id_usuario] = $obj->id_usuario."|".$obj->nombre;
        }

        return $arr;
    }
}